import React, { useCallback, useState } from "react";
import AppLayout from "../components/AppLayout";
import { Form, Input } from "antd";
import Head from "next/head";

const Signup = () => {
const [id,onChangeId]=useInput('');

  // const [id, setId] = useState("");
  // const onChangedId = useCallback(e => {
  //   setId(e.target.value);
  // }, []);

  // const [nickname, setNickname] = useState("");
  // const onChangedNickname = useCallback(e => {
  //   setNickname(e.target.value);
  // }, []);

  // const [password, setPassword] = useState("");
  // const onChangedPassword = useCallback(e => {
  //   setPassword(e.target.value);
  // }, []);

  return (
    <AppLayout>
      <Head>
        <title>회원가입 | NodeBird</title>
      </Head>
      <Form onFinish={onsubmit}>
        <div>
          <label htmlFor="user-id">아이디</label>
          <br />
          <Input name="user-id" value={id} required onChange={onChangedId} />
        </div>
        <div>
          <label htmlFor="user-nick">닉네임</label>
          <br />
          <Input
            name="user-nick"
            value={nickname}
            required
            onChange={onChangedNickname}
          />
        </div>
        <div>
          <label htmlFor="user-password">비밀번호</label>
          <br />
          <Input
            name="user-password"
            type="password"
            value={password}
            required
            onChange={onChangedPassword}
          />
        </div>
        <div>
          <label htmlFor="user-password-check">비밀번호 체크</label>
          <br />
          <Input
            name="user-password-check"
            type="password"
            value={passwordCheck}
            required
            onChange={onChangedPasswordCheck}
          />
        </div>
      </Form>
    </AppLayout>
  );
};

export default Signup;

